import React, { useState, useEffect } from "react";

const MyAccordian = ({ question, answer }) => {
  return (
    <>
      <div
        className="myaccordian"
        style={{ backgroundColor: "green", width: "50%" }}
      >
        <p>+</p>
        <h1>{question}</h1>
      </div>

      <p>{answer}</p>
    </>
  );
};
export default MyAccordian;
