const MyAccordian = ({ question, answer }) => {
  const [show,setShow]=useState(false);
  return (
    <>
      <div  className="my-container">
        <div className="myaccordian">
          <p className="plus">
            <BiPlusCircle size={30} />
          </p>
{show &&  <h1>{question}</h1> }

        </div>

        <p>{answer}</p>
      </div>
    </>
  );
};
export default MyAccordian;
