import React, { useState, useEffect } from "react";

const MyAccordian = ({ question, answer }) => {
  return (
    <>
      <div style={{ width: "50%" }}>
        <div style={{ backgroundColor: "green", width: "50%" }}>
          <p>+</p>
          <h1>{question}</h1>
        </div>

        <p>{answer}</p>
      </div>
    </>
  );
};
export default MyAccordian;
