import "./MyAccordian.scss";

const MyAccordian = ({ question, answer }) => {
  return (
    <>
      <div className="myaccordian">
        <div

        >
          <p className="plus">
            <i class="fa fa-plus-circle" aria-hidden="true"></i>
          </p>
          <h1>{question}</h1>
        </div>

        <p>{answer}</p>
      </div>
    </>
  );
};
export default MyAccordian;
